function getNavHeight(){return $("nav").outerHeight()}function headerHeight(){$header=$("header"),$navBar=getNavHeight(),$($header).css("height","calc(100vh - "+$navBar+"px")}function addDropdownClass(e){$(e+" ul li:has(ul)").addClass("hasDropdown"),$(".hasDropdown").on("click",function(e){e.preventDefault(),$(this).toggleClass("open").find("ul").slideToggle()})}function displayMobileNavNar(){$(".menu-icon").on("click",function(e){e.preventDefault(),$(".mob-nav-holder").slideToggle("slow")})}function addTwitter(e){$(e+" ul.main-nav > li:last-child").after('<li><a href="http://twitter.com"><i class="social-btn fa fa-twitter"></i></a></li>')}function addTweetSlider(){$(".tweet-slide").slick({dots:!1,infinite:!0,speed:1e3,slidesToShow:1,slidesToScroll:1,autoplaySpeed:4e3,arrows:!0,adaptiveHeight:!0,appendArrows:$(".slider-arrows"),nextArrow:"<div class='slider-arrow next-arrow'></div>",prevArrow:"<div class='slider-arrow prev-arrow'></div>"})}$(document).ready(function(){console.log("jQuery is setup and running!"),headerHeight(),addDropdownClass("nav"),addTwitter("nav"),displayMobileNavNar(),addTweetSlider()}),$(window).resize(function(){1023<$(window).width()&&$(".mob-nav-holder").slideUp()});