function getNavHeight(){return $("nav").outerHeight()}function headerHeight(){$header=$("header"),$navBar=getNavHeight(),$($header).css("height","calc(100vh - "+$navBar+"px")}function addDropdownClass(a){$(a+" ul li:has(ul)").addClass("hasDropdown"),$(".hasDropdown").on("click",function(a){a.preventDefault(),$(this).toggleClass("open").find("ul").slideToggle()})}function addTwitter(a){$(a+" ul.main-nav > li:last-child").after('<li><a href="http://twitter.com"><i class="social-btn fa fa-twitter"></i></a></li>')}$(document).ready(function(){console.log("jQuery is setup and running!"),headerHeight(),addDropdownClass("nav"),addTwitter("nav")});